function diftemps(temp_final, temp_inici) {
    if (temp_final !== undefined) {
        var timeDifference = temp_final - temp_inici;
        var differenceDate = new Date(timeDifference * 1000);
        var diffHours = differenceDate.getUTCHours();
        var diffMinutes = differenceDate.getUTCMinutes();
        var diffSeconds = differenceDate.getUTCSeconds();

        if (diffMinutes == 0) {
            return diffSeconds + ' s';

        } else if (diffHours == 0) {
            return diffMinutes + ' m : ' + diffSeconds + ' s';
        } else {
            return diffHours + ' h : ' + diffMinutes + ' m : ' + diffSeconds + ' s';
        }
    } else {
        let tempnow = Math.round(Date.now() / 1000);
        var timeDifference = tempnow - temp_inici;
        var differenceDate = new Date(timeDifference * 1000);
        var diffHours = differenceDate.getUTCHours();
        var diffMinutes = differenceDate.getUTCMinutes();
        var diffSeconds = differenceDate.getUTCSeconds();

        if (diffMinutes == 0) {
            return diffSeconds + ' s';

        } else if (diffHours == 0) {
            return diffMinutes + ' m : ' + diffSeconds + ' s';
        } else {
            return diffHours + ' h : ' + diffMinutes + ' m : ' + diffSeconds + ' s';
        }
    }

}

function mode(arr) {
    const mode = {};
    let max = 0, count = 0;

    for (let i = 0; i < arr.length; i++) {
        const item = arr[i];

        if (mode[item]) {
            mode[item]++;
        } else {
            mode[item] = 1;
        }

        if (count < mode[item]) {
            max = item;
            count = mode[item];
        }
    }

    return max;
};

function climateuse(starttimetravel, endtimetravel, timeAc, onclima) {
    let hores_ac = 0;
    let minuts_ac = 0;
    let seg_ac = 0;

    let hores_v = 0;
    let minuts_v = 0;
    let seg_v = 0;

    if (timeAc == 0 && onclima == 0) {
        return "A/C: Apagdo"
    } else if (timeAc > 0 && onclima > 0) {

        let time = Date.now();
        let diftime = timeAc + (time - onclima);
        let lasttime = new Date(diftime);
        hores_ac += lasttime.getUTCHours();
        minuts_ac += lasttime.getUTCMinutes();
        seg_ac += lasttime.getUTCSeconds();

    } else if (onclima > 0) {

        let time3 = Date.now();
        let diftime = time3 - onclima;
        let lasttime = new Date(diftime);
        hores_ac += lasttime.getUTCHours();
        minuts_ac += lasttime.getUTCMinutes();
        seg_ac += lasttime.getUTCSeconds();

    } else if (timeAc > 0) {

        let time = new Date(timeAc);
        hores_ac += time.getUTCHours();
        minuts_ac += time.getUTCMinutes();
        seg_ac += time.getUTCSeconds();
    }

    if (endtimetravel !== undefined) {
        var timeDifference = endtimetravel - starttimetravel;
        var differenceDate = new Date(timeDifference * 1000);
        hores_v = differenceDate.getUTCHours();
        minuts_v = differenceDate.getUTCMinutes();
        seg_v = differenceDate.getUTCSeconds();
    } else {
        let tempnow = Math.round(Date.now() / 1000);
        var timeDifference = tempnow - starttimetravel;
        var differenceDate = new Date(timeDifference * 1000);
        hores_v = differenceDate.getUTCHours();
        minuts_v = differenceDate.getUTCMinutes();
        seg_v = differenceDate.getUTCSeconds();
    }

    let htm = hores_ac * 60;
    let stm = seg_ac / 60;
    let totalmin = minuts_ac + htm + stm;
    let htm_v = hores_v * 60;
    let stm_v = seg_v / 60;
    let totalmin_v = minuts_v + htm_v + stm_v;
    let useAC = Math.round(totalmin * 100 / totalmin_v);

    if (hores_ac == 0 && minuts_ac == 0) {
        return `A/C: ${seg_ac} s (${useAC}%)`
    } else if (hores_ac == 0) {
        return `A/C: ${minuts_ac} m : ${seg_ac} s (${useAC}%)`
    } else {
        return `A/C: ${hores_ac} h : ${minuts_ac} m : ${seg_ac} s (${useAC}%)`
    }
}

function battery(end_lvl, start_lvl, energyUsed, km_inici, km_final) {
    if (end_lvl == undefined) {
        let lvl_now = flow.get('battery_level');
        return `Inicial: ${start_lvl}% 
                Actual: ${lvl_now}%  
                Utilizada: ${(lvl_now - start_lvl) * -1}% `
    } else {
        if (energyUsed != -100) {
            let consum = (energyUsed / (km_final - km_inici) * 1000).toFixed(0);
            return `Inicial: ${start_lvl}% 
                Final: ${end_lvl}% 
                Utilitzada: ${(end_lvl - start_lvl) * -1}%
                Energ√≠a usada: ${energyUsed} kWh
                Consumo: ${consum} Wh/km`
        } else {
            return `Inicial: ${start_lvl}% 
                Final: ${end_lvl}% 
                Utilizada: ${(end_lvl - start_lvl) * -1}%`
        }
    }
}

function desnivell(array) {
    var desnivell_positiu = 0;
    var desnivell_negatiu = 0;

    for (var i = 1; i < array.length; i++) {
        var diferencial = array[i] - array[i - 1];
        if (diferencial > 0) {
            desnivell_positiu += diferencial;
        }
        else {
            desnivell_negatiu -= diferencial;
        }
    }
    return `Positivo: ${desnivell_positiu} m
                Negativo: ${desnivell_negatiu * -1} m
                Acumulado: ${desnivell_positiu + desnivell_negatiu} m`
}

